import React from 'react';
import "../Toasts/Toast.scss";


export default function Toast({type, title, setShow}) {

  return (
    <div className='toast_container' style={{position: "absolute"}}>
        <h1>Toast Notification</h1>
        <button onClick={setShow(false)}>close</button>
    </div>
  )
}
