import "../Myform/Myform.scss";
import Button from '../../Components/Buttons/Button';
import {db} from  "../../Firebase_configuration"
import {collection ,addDoc} from "firebase/firestore"

export default function Myform() {

  const [projectName, setProjectName] = useState
  const [projectStatus, setProjectStatus] = useState
  const [projectYear, setProjectYear] = useState
  const [projectTools,setProjectTools] = useState
  const [projectHref,setProjectHref] = useState
  const [projectImage, setProjectImage] = useState
  const projectCollectionRef = collection(db, "Projects");

  // const handleSubmit = (e) => {
  //    e.preventDefault();
  //   addDoc(projectCollectionRef, { ProjectName: projectName,
  //      : text, Date: date });
  //   // setText("");
  //   // setTitle("");
  // };

  return (
    <div className='container-fluid upload_container'>
        <h1>PROJECT UPLOADER</h1>
        <div className='container inner_upload_container'>
        <form action="">
            <input type="text" placeholder='Project Name' className='form-control' onChange={(e) => setProjectName (e.target.value)}/>
            <select name="Project Status" id="" placeholder='Project Status' onChange={(e)=>setProjectStatus(selec)}>
              <option defaultvalue="Empty" disabled selected hidden>Project Status</option>
              <option value="complete">Complete</option>
              <option value="Incomplete">Incomplete</option>
            </select>
            <input type="text" placeholder='Project year' className="form-control"/>
            <input type="text" placeholder='Project Stools' className='form-control'/>
            <input type="text" placeholder='Project href' className='form-control'/>
            <label htmlFor="imageupload">
              <div className='imagebox'>
                <h4>Click here to Upload Image</h4>
              </div>
            </label>
            <input type="file" id="imageupload"  accept="image/*"  style={{display: "none"}}/>
            <Button title={"Upload Project"} type="button"/>
        </form>
        </div>
    </div>
  )
}
