import React from 'react';
import "../Toasts/Toast.scss";


export default function Toast({setShow, show}) {

  return (
    <div className={`toast_container animate__animated animate__bounceInLeft ${!show && "bounceOutRight" }`}>
        <span>Thanks, your project has being sent, expect our Response sooner!</span>
        <button onClick={setShow(!show)}>close</button>
    </div>
  )
}
