import React, { useState } from "react";
import "../Myform/Myform.scss";
import Button from "../../Components/Buttons/Button";
import { db } from "../../Firebase_configuration";
import { collection, addDoc } from "firebase/firestore";

export default function Myform() {
  const [projectName, setProjectName] = useState;
  const [projectStatus, setProjectStatus] = useState;
  const [projectYear, setProjectYear] = useState;
  const [projectTools, setProjectTools] = useState;
  const [projectHref, setProjectHref] = useState;
  const [projectImage, setProjectImage] = useState;
  const projectCollectionRef = collection(db, "Projects");

  // const handleSubmit = (e) => {
  //    e.preventDefault();
  //   addDoc(projectCollectionRef, { ProjectName: projectName,
  //     ProjectStatus: projectStatus, Projectyear: projectYear,
  //     Projecttools: projectTools, Projecthref: projectHref,
  //     Projectimage: projectImage });
  //     setProjectName("");
  //     setProjectStatus("");
  //     setProjectTools("");
  //     setProjectHref("");
  //     setProjectImage("")
  // };

  return (
    <div className="container-fluid upload_container">
      <h1>PROJECT UPLOADER</h1>
      <div className="container inner_upload_container">
        <form>
          <input
            type="text"
            placeholder="Project Name"
            className="form-control"
            onChange={(e) => setProjectName(e.target.value)}
          />
          <select
            name="Project Status"
            id=""
            placeholder="Project Status"
            onChange={(e) => setProjectStatus(e.target.value)}>
            <option defaultvalue="Empty" disabled selected hidden>
              Project Status
            </option>
            <option value="complete">Complete</option>
            <option value="Incomplete">Incomplete</option>
          </select>
          <input
            type="text"
            placeholder="Project year"
            className="form-control"
            onChange={(e) => setProjectYear(e.target.value)}
          />
          <input
            type="text"
            placeholder="Project Stools"
            className="form-control"
            onChange={(e) => setProjectTools(e.target.value)}
          />
          <input
            type="text"
            placeholder="Project href"
            className="form-control"
            onChange={(e) => setProjectHref(e.target.value)}
          />
          <label htmlFor="imageupload">
            <div className="imagebox">
              <h4>Click here to Upload Image</h4>
            </div>
          </label>
          z
          <input
            type="file"
            id="imageupload"
            accept="image/*"
            style={{ display: "none" }}
            onChange={(e) => setProjectImage(e.target.files[0])}
          />
          <Button title={"Upload Project"} type="button" />
        </form>
      </div>
    </div>
  );
}
